import { Component } from '@angular/core';
import { Cliente } from '../modelo/Cliente';
import { ClienteService } from '../servico/cliente.service';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})
export class PrincipalComponent {

    btnCadastro:boolean = true;

    tabela:boolean = true;

    cliente = new Cliente();

    //Json de cliente
    clientes:Cliente[] = [];

    constructor(private servico:ClienteService){}

    //metodo de selecao total
    selecionar():void{
      this.servico.selecionar().subscribe(retorno => this.clientes = retorno)
    }

    //metodo de cadastro
    cadastrar():void{
      this.servico.cadastrar(this.cliente).subscribe(retorno => {
        this.clientes.push(retorno);
        //limpar formulario
        this.cliente = new Cliente();
        alert(`Cliente Cadastrado com Sucesso!`)

      });
    }

    //metodo de edição
    editar():void{
      this.servico.editar(this.cliente).subscribe(retorno => {
        let posicao = this.clientes.findIndex(obj =>{
          return obj.codigo == retorno.codigo;
        })
        this.clientes[posicao]= retorno;
        //limpar formulario
        this.cliente = new Cliente();
        alert(`Cliente Alterado com Sucesso!`)
        this.btnCadastro = true;
        this.tabela = true;

      });
    }

//metodo de remocao
  remover():void{
  this.servico.remover(this.cliente.codigo).subscribe(retorno => {

    let posicao = this.clientes.findIndex(obj =>{
      return obj.codigo == this.cliente.codigo;
    })

    this.clientes.splice(posicao)

    //limpar formulario
    this.cliente = new Cliente();
    alert(`Cliente Removido com Sucesso!`)
    this.btnCadastro = true;
    this.tabela = true;

  });
}

    //metodo para selectao de cliente
    selecionarCliente(posicao:number):void{
      this.cliente = this.clientes[posicao];

      this.btnCadastro = false;
      this.tabela = false;

    }

    cancelar():void{
      this.btnCadastro = true;
      this.tabela = true;

    }

    //metodo de inicializacao
    ngOnInit(){
      this.selecionar();
    }


}
<form>
    <img src="assets/cliente.png" alt="">
    <input type="text" placeholder="Nome" class="form-control" name="nome" [(ngModel)]="cliente.nome">
    <input type="number" placeholder="Idade" class="form-control" name="idade" [(ngModel)]="cliente.idade">
    <input type="text" placeholder="Cidade" class="form-control" name="cidade" [(ngModel)]="cliente.cidade">
    <input type="button" value="Cadastrar" *ngIf="btnCadastro" class="btn btn-primary" (click)="cadastrar()">
    <input type="button" value="Alterar" *ngIf="!btnCadastro" class="btn btn-warning" (click)="editar()">
    <input type="button" value="Remover" *ngIf="!btnCadastro" class="btn btn-danger" (click)="remover()">
    <input type="button" value="Cancelar" *ngIf="!btnCadastro" class="btn btn-secondary" (click)="cancelar()">
</form>

<table class="table" *ngIf="tabela">
    <thead>
        <tr>
            <th>#</th>
            <th>Nome</th>
            <th>Idade</th>
            <th>Cidade</th>
            <th>Selecionar</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let c of clientes;index as i">
            <td>{{i+1}}</td>
            <td>{{c.nome}}</td>
            <td>{{c.idade}}</td>
            <td>{{c.cidade}}</td>
            <td><button class="btn btn-success" (click)="selecionarCliente(i)">Selecionar</button></td>
        </tr>
    </tbody>
</table>
