import { Component } from '@angular/core';
import { Cliente } from '../modelo/Cliente';
import { ClienteService } from '../servico/cliente.service';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})
export class PrincipalComponent {

    btnCadastro:boolean = true;

    cliente = new Cliente();

    //Json de cliente
    clientes:Cliente[] = [];

    constructor(private servico:ClienteService){}

    //metodo de selecao
    selecionar():void{
      this.servico.selecionar().subscribe(retorno => this.clientes = retorno)
    }

    //metodo de cadastro
    cadastrar():void{
      this.servico.cadastrar().subscribe(retorno => this.clientes = retorno)
    }

    //metodo de inicializacao
    ngOnInit(){
      this.selecionar();
    }


}
<form>
    <img src="assets/cliente.png" alt="">
    <input type="text" placeholder="Nome" class="form-control" name="nome" [(ngModel)]="cliente.nome">
    <input type="number" placeholder="Idade" class="form-control" name="idade" [(ngModel)]="cliente.idade">
    <input type="text" placeholder="Cidade" class="form-control" name="cidade" [(ngModel)]="cliente.cidade">
    <input type="button" value="Cadastrar" *ngIf="btnCadastro" class="btn btn-primary">
    <input type="button" value="Alterar" *ngIf="!btnCadastro" class="btn btn-warning">
    <input type="button" value="Remover" *ngIf="!btnCadastro" class="btn btn-danger">
    <input type="button" value="Cancelar" *ngIf="!btnCadastro" class="btn btn-secondary">
</form>
<h1>{{cliente | json}}</h1>
<table class="table">
    <thead>
        <tr>
            <th>#</th>
            <th>Nome</th>
            <th>Idade</th>
            <th>Cidade</th>
            <th>Selecionar</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let c of clientes;index as i">
            <td>{{i+1}}</td>
            <td>{{c.nome}}</td>
            <td>{{c.idade}}</td>
            <td>{{c.cidade}}</td>
            <td><button class="btn btn-success">Selecionar</button></td>
        </tr>
    </tbody>
</table>
